print("Part One:", sum([1 if (pair[0][0] <= pair[1][0] and pair[0][1] >= pair[1][1]) or (pair[1][0] <= pair[0][0] and pair[1][1] >= pair[0][1]) else 0 for pair in [[[int(item) for item in pair[0].split('-')], [int(item) for item in pair[1].split('-')]] for pair in [line.strip().split(",") for line in open("4/input.txt")]]]))
print("Part Two:", sum([1 if (pair[0][0] <= pair[1][0] <= pair[0][1]) or (pair[0][0] <= pair[1][1] <= pair[0][1]) or (pair[1][0] <= pair[0][0] <= pair[1][1]) or (pair[1][0] <= pair[0][1] <= pair[1][1]) else 0 for pair in [[[int(item) for item in pair[0].split('-')], [int(item) for item in pair[1].split('-')]] for pair in [line.strip().split(",") for line in open("4/input.txt")]]]))